figure.artwork:
-- Notifications --

pcePcepPathKeyNotifications OBJECT IDENTIFIER ::= 
                        { pcePcepPathKeyDraftMIB 0 }

pcePcepPathKeyMIBObjects OBJECT IDENTIFIER ::= 
                        { pcePcepPathKeyDraftMIB 1 }
pcePcepPathKeyConformance   OBJECT IDENTIFIER ::= 
                        { pcePcepPathKeyDraftMIB 2 }

pcePcepPathKeyObjects OBJECT IDENTIFIER ::= 
                        { pcePcepPathKeyMIBObjects 1 }

pcePcepPathKeyRetainStatus OBJECT-TYPE
            SYNTAX      INTEGER {
                       enabled(1),
                       disabled(2)
                     }
            MAX-ACCESS  read-write
            STATUS      optional
            DESCRIPTION
             "The path-key retain status of this PCE to retain the 
             path-key and CPS for debugging purposes."
            DEFVAL {disabled(2)}    
            ::= {  pcePcepPathKeyObjects 3 }

pcePcepPathKeyConfig OBJECT-TYPE
               SYNTAX      INTEGER {
                       enabled(1),
                       disabled(2)
                     }
               MAX-ACCESS  read-write
               STATUS      mandatory
               DESCRIPTION
                   "The path-key based inter domain computation 
                    configuration."
               DEFVAL {disabled(2)}     
               ::= {  pcePcepPathKeyObjects 11 }

pcePcepPathKeyEntry OBJECT-TYPE SYNTAX pcePcepPathKeyEntry MAX-ACCESS not-accessible STATUS current DESCRIPTION "An entry in this table represents a path-key and CPS. An entry is only created when a path-key generated by PCE during inter-domain computation."
INDEX { pcePcepPathKey }
::= { pcePcepPathKeyTable 1 }

pcePcepPathKeyHopEntry OBJECT-TYPE SYNTAX pcePcepPathKeyHopEntry MAX-ACCESS not-accessible STATUS current DESCRIPTION "An entry in this table represents a Hop in the CPS. An entry is only created when a path-key generated by PCE during inter-domain computation."
INDEX { pcePcepPathKeyHopListIndex, pcePcepPathKeyHopIndex }
::= { pcePcepPathKeyHopTable 1 }

--**************************************************************** -- Module Conformance Statement --****************************************************************
pcePcepPathKeyGroups OBJECT IDENTIFIER ::= { pcePcepPathKeyConformance 1 }
pcePcepPathKeyCompliances OBJECT IDENTIFIER ::= { pcePcepPathKeyConformance 2 }

-- -- Full Compliance --
pcePcepPathKeyModuleFullCompliance MODULE-COMPLIANCE STATUS current DESCRIPTION "The Module is implemented with support for read-create and read-write. In other words, both monitoring and configuration are available when using this MODULE-COMPLIANCE."
MODULE -- this module MANDATORY-GROUPS { pcePcepPathKeyGeneralGroup, pcePcepPathKeyNotificationsGroup }
::= { pcePcepPathKeyCompliances 1 }

-- -- Read-Only Compliance --
pcePcepPathKeyModuleReadOnlyCompliance MODULE-COMPLIANCE STATUS current DESCRIPTION "The Module is implemented with support for read-only. In other words, only monitoring is available by implementing this MODULE-COMPLIANCE."
MODULE -- this module MANDATORY-GROUPS { pcePcepPathKeyGeneralGroup, pcePcepPathKeyNotificationsGroup } ::= { pcePcepPathKeyCompliances 2 }

pcePcepPathKeyNotificationsGroup NOTIFICATION-GROUP NOTIFICATIONS { pcePcepPathKeyExpandUnknownNtf, pcePcepPathKeyExpandExpiredNtf } STATUS current
DESCRIPTION "The notifications for a PCEP Pathkey MIB implementation." ::= { pcePcepPathKeyGroups 2 }
END
