                                     { pcePcepPathKeyMIBObjects 1 }

pcePcepPathKeyRetainStatus OBJECT-TYPE
            SYNTAX      INTEGER {
                       enabled(1),
                       disabled(2)
                     }
            MAX-ACCESS  read-only
            STATUS      optional
            DESCRIPTION
                "The path-key retain status of this PCE to retain the 
                 path-key and CPS after retrieval."
            DEFVAL {disabled(2)}    
            ::= {  pcePcepPathKeyObjects 3 }

pcePcepPathKeyConfig OBJECT-TYPE
             SYNTAX      INTEGER {
                       enabled(1),
                       disabled(2)
                     }
             MAX-ACCESS  read-only
             STATUS      mandatory
             DESCRIPTION
                 "Path-key based confidentiality is enabled."
             DEFVAL {disabled(2)}     
             ::= {  pcePcepPathKeyObjects 11 }

pcePcepPathKeyEntry OBJECT-TYPE SYNTAX pcePcepPathKeyEntry MAX-ACCESS not-accessible STATUS current DESCRIPTION "An entry in this table represents a path-key and CPS. An entry is only created when a path-key is generated by PCE during inter-domain path computation."
INDEX { pcePcepPathKey }
::= { pcePcepPathKeyTable 1 }

