enum { reservedAddr(0), ipv4_address(1), ipv6_address(2),
     (255) } AddressType;

struct {
  uint32                  addr;
  uint16                  port;
} IPv4AddrPort;

struct {
  uint128                 addr;
  uint16                  port;
} IPv6AddrPort;


struct {
  AddressType             type;
  uint8                   length;

  select (type) {
    case ipv4_address:
       IPv4AddrPort       v4addr_port;

    case ipv6_address:
       IPv6AddrPort       v6addr_port;

    /* This structure can be extended */
  };
} IpAddressPort;

