enum { reserved(0),
       peer_ready(1), neighbors(2), full(3), (255) }
     ChordUpdateType;


struct {
   uint32                uptime; 
   ChordUpdateType         type;
   select (type){
    case peer_ready:                   /* Empty */
      ;

    case neighbors:
      NodeId              predecessors<0..2^16-1>;
      NodeId              successors<0..2^16-1>;

    case full:
      NodeId              predecessors<0..2^16-1>;
      NodeId              successors<0..2^16-1>;
      NodeId              fingers<0..2^16-1>;             
  };
} ChordUpdate;

