figure.artwork:
URN       = "urn" ":" NID ":" NSS
          ;
          ; the URI scheme ("urn") is case-insensitive
          ;
NID       = (alphanum) 0*30(ldh) (alphanum)
          ;
          ; alphanum is defined in RFC 3986
          ;
ldh       = alphanum / "-"
NSS       = 1*(pchar)
          ;
          ; pchar is defined in RFC 3986
          ;
