tunnelIfXTable OBJECT-TYPE SYNTAX SEQUENCE OF TunnelIfXEntry MAX-ACCESS read-write STATUS current DESCRIPTION "This table contains additional objects for the tunnel interface table." ::= { tunnel xx }
tunnelIfXEntry OBJECT-TYPE SYNTAX TunnelIfXEntry MAX-ACCESS read-write STATUS current DESCRIPTION "An entry containing additional information applicable to a particular tunnel interface." INDEX { ifIndex } ::= { tunnelIfXTable 1 }
TunnelIfXEntry ::= SEQUENCE { SamPrex InetAddress, SamLength Integer32 BasePrex InetAddress, BaseLength Integer32 EAbit Integer32 PSID Integer32 } }
SamPrefix OBJECT-TYPE SYNTAX InetAddress MAX-ACCESS read-write STATUS current DESCRIPTION "Stateless Adress Mapping Prex IPv4 for MAP,IPv6 for 6rd" := { TunnelIfXEntry 1 }
SamLength OBJECT-TYPE SYNTAX Integer32(0..127) MAX-ACCESS read-write STATUS current DESCRIPTION "Stateless Adress Mapping length IPv4(0-31) for MAP,IPv6(0-127) for 6rd" := { TunnelIfXEntry 2 }
BasePrefix OBJECT-TYPE SYNTAX InetAddress MAX-ACCESS read-write STATUS current DESCRIPTION "rule IPv6 prefix for MAP, IPv4 address for 6rd" := { TunnelIfXEntry 3 }
BaseLength OBJECT-TYPE SYNTAX InetAddress MAX-ACCESS read-write STATUS current DESCRIPTION "rule IPv6 prefix for MAP, IPv4 address for 6rd" := { TunnelIfXEntry 4 }
EAbit OBJECT-TYPE SYNTAX Integer32(0..127) MAX-ACCESS read-write STATUS current DESCRIPTION "rule IPv6 prefix length for MAP, IPv4MaskLength for 6rd" := { TunnelIfXEntry 5 }
PSID OBJECT-TYPE SYNTAX Integer32(0..127) MAX-ACCESS read-write STATUS current DESCRIPTION "EA bit for MAP,0 must be for 6rd" := { TunnelIfXEntry 6 }
