struct ssctp_init {
       uint16_t  secsessID;
       uint16_t  key_length;
       uint8_t   num_cipher;
       uint8_t   *cipher_suites;
       uint8_t   num_comp;
       uint8_t   *comp_methods;
       uint8_t   *certificate;
};

struct ssctp_seclevel {
       uint16_t  secsessID;
       uint8_t  seclevel;
};

struct ssctp_secstatus {
       uint16_t secsessID;
       uint8_t sec_status;
};

struct ssctp_secsessTTL {
       uint16_t secsessID;
       uint16_t secsessTTL;
};
