-- ---------------------------------------------------------- -- -- subtrees in the SNMP-TRANSPORT-SM-MIB -- ---------------------------------------------------------- --
snmpTsmNotifications OBJECT IDENTIFIER ::= { snmpTsmMIB 0 } snmpTsmMIBObjects OBJECT IDENTIFIER ::= { snmpTsmMIB 1 } snmpTsmConformance OBJECT IDENTIFIER ::= { snmpTsmMIB 2 }
-- ------------------------------------------------------------- -- Objects -- -------------------------------------------------------------
-- Statistics for the Transport Security Model
snmpTsmStats OBJECT IDENTIFIER ::= { snmpTsmMIBObjects 1 }
snmpTsmInvalidCaches OBJECT-TYPE SYNTAX Counter32 MAX-ACCESS read-only STATUS current DESCRIPTION "The number of messages dropped because the tmStateReference referred to an invalid cache. " ::= { snmpTsmStats 1 }
snmpTsmInadequateSecurityLevels OBJECT-TYPE SYNTAX Counter32 MAX-ACCESS read-only STATUS current DESCRIPTION "The number of incoming messages dropped because the actual securityLevel provided was less than the requested securityLevel. " ::= { snmpTsmStats 2 }
-- ------------------------------------------------------------- -- snmpTsmMIB - Conformance Information -- -------------------------------------------------------------
snmpTsmCompliances OBJECT IDENTIFIER ::= { snmpTsmConformance 1 }
snmpTsmGroups OBJECT IDENTIFIER ::= { snmpTsmConformance 2 }
-- ------------------------------------------------------------- -- Compliance statements -- -------------------------------------------------------------
snmpTsmCompliance MODULE-COMPLIANCE STATUS current DESCRIPTION "The compliance statement for SNMP engines that support the SNMP-TRANSPORT-SM-MIB" MODULE MANDATORY-GROUPS { snmpTsmGroup } ::= { snmpTsmCompliances 1 }
-- ------------------------------------------------------------- -- Units of conformance -- ------------------------------------------------------------- snmpTsmGroup OBJECT-GROUP OBJECTS { snmpTsmInvalidCaches, snmpTsmInadequateSecurityLevels } STATUS current DESCRIPTION "A collection of objects for maintaining information of an SNMP engine which implements the SNMP Transport Security Model. "
::= { snmpTsmGroups 2 }
