-- ## Definition of the user transport address list table ######### rserpoolPEUserAddrTable OBJECT-TYPE SYNTAX SEQUENCE OF RserpoolPEUserAddrTableEntry MAX-ACCESS not-accessible STATUS current DESCRIPTION "A table listing of all IP addresses of the user transport endpoint." ::= { rserpoolPoolElements 6 }
rserpoolPEUserAddrTableEntry OBJECT-TYPE SYNTAX RserpoolPEUserAddrTableEntry MAX-ACCESS not-accessible STATUS current DESCRIPTION "An IP address of the user transport endpoint." INDEX { rserpoolPEIndex, rserpoolPEUserAddrTableIndex } ::= { rserpoolPEUserAddrTable 1 }
RserpoolPEUserAddrTableEntry ::= SEQUENCE { rserpoolPEUserAddrTableIndex Unsigned32, rserpoolPEUserL3Type InetAddressType, rserpoolPEUserL3Addr InetAddress, rserpoolPEUserL3Opaque RSerPoolOpaqueAddressTC }
rserpoolPEUserAddrTableIndex OBJECT-TYPE SYNTAX Unsigned32 (1..4294967295) MAX-ACCESS not-accessible STATUS current DESCRIPTION "A unique identifier for the IP address of an user transport endpoint." ::= { rserpoolPEUserAddrTableEntry 1 }
rserpoolPEUserL3Type OBJECT-TYPE SYNTAX InetAddressType { ipv4(1), ipv6(2) } MAX-ACCESS read-only STATUS current DESCRIPTION "The network-layer protocol of an IP address of an user transport endpoint. Set to unknown for opaque address." ::= { rserpoolPEUserAddrTableEntry 2 }
rserpoolPEUserL3Addr OBJECT-TYPE SYNTAX InetAddress (SIZE(4|16)) MAX-ACCESS read-only STATUS current DESCRIPTION "The IP address of an user transport endpoint. The type of this address is given in rserpoolPEUserL3Addr." ::= { rserpoolPEUserAddrTableEntry 3 }
rserpoolPEUserL3Opaque OBJECT-TYPE SYNTAX RSerPoolOpaqueAddressTC MAX-ACCESS read-only STATUS current DESCRIPTION "The opaque address of an user transport endpoint." ::= { rserpoolPEUserAddrTableEntry 4 }
