figure.artwork:
ContentInfo ::= SEQUENCE { 
  contentType ContentType, 
  content [0] EXPLICIT ANY DEFINED BY contentType } 

ContentType ::= OBJECT IDENTIFIER

SignedData ::= SEQUENCE { version CMSVersion, digestAlgorithms DigestAlgorithmIdentifiers, encapContentInfo EncapsulatedContentInfo, certificates [0] IMPLICIT CertificateSet OPTIONAL, crls [1] IMPLICIT RevocationInfoChoices OPTIONAL, signerInfos SignerInfos }
DigestAlgorithmIdentifiers ::= SET OF DigestAlgorithmIdentifier
SignerInfos ::= SET OF SignerInfo

EncapsulatedContentInfo ::= SEQUENCE { 
  eContentType ContentType, 
  eContent [0] EXPLICIT OCTET STRING OPTIONAL } 

ContentType ::= OBJECT IDENTIFIER

id-smime OBJECT IDENTIFIER ::= { iso(1) member-body(2) us(840) 
    rsadsi(113549) pkcs(1) pkcs9(9) 16 } 

id-ct OBJECT IDENTIFIER ::= { id-smime 1 } 

id-ct-rpkiBOA OBJECT IDENTIFIER ::= { id-ct [TBD] }

id-ct-rpkiBOA ::= { 
   version [0] INTEGER DEFAULT 0, 
   asIDs        SEQUENCE OF asIdsOrRange, 
   ipAddrBlocks SEQUENCE OF BOAIPAddressFamily }

ASIdOrRange   ::= CHOICE {
   id               ASId,
   range            ASRange }

ASRange       ::= SEQUENCE {
   min              ASId,
   max              ASId }

ASId          ::= INTEGER

BOAIPAddressFamily ::= SEQUENCE { 
   addressFamily OCTET STRING (SIZE (2..3)), 
   addresses SEQUENCE OF IPAddress } 

IPAddress ::= BIT STRING

SignerIdentifier ::= CHOICE { 
  issuerAndSerialNumber IssuerAndSerialNumber, 
  subjectKeyIdentifier [0] SubjectKeyIdentifier }

SignedAttributes ::= SET SIZE (1..MAX) OF Attribute

Attribute ::= SEQUENCE {
  attrType OBJECT IDENTIFIER,
  attrValues SET OF AttributeValue }

AttributeValue ::= ANY

id-signingTime OBJECT IDENTIFIER ::= { iso(1) member-body(2)
    us(840) rsadsi(113549) pkcs(1) pkcs9(9) 5 }

SigningTime ::= Time

Time ::= CHOICE {
  utcTime UTCTime,
  generalizedTime GeneralizedTime }

id-aa-binarySigningTime OBJECT IDENTIFIER ::= { iso(1) member-body(2) us(840) rsadsi(113549) pkcs(1) pkcs9(9) smime(16) aa(2) 46 }
BinarySigningTime ::= BinaryTime
BinaryTime ::= INTEGER (0..MAX)
