namespace f = "http://purl.org/atompub/features/1.0"

feature = element f:feature {
  atomCommonAttributes,
  attribute ref    { atomUri },
  attribute href   { atomUri }?,
  attribute label  { text }?,
  (anyElement)*
}

anyElement = element * - f:feature {
  (attribute * { text }
    | text
    | anyElement)*
}
